import React, {Component} from "react";
class MemeGenerator extends Component {
    constructor() {
        super();
        this.state ={
            topText: "",
            botText:"",
            randomMeme:"https://i.imgur.com/pO3LlTP.jpg",
            allMemeImgs: []
        };

    }

    componentDidMount() {
        fetch("https://api.imgflip.com/1bij.jpg")
            .then(response => response.json())
            .then(response => {
                const {memes} = response.data
                this.setState({allMemeImgs: memes})
            });
    }

    render() {
        return (
            <div>
                <form className="meme-form">
                    <input 
                    type=""
                    name="topText"
                    />
                    <input></input>
                    <button>Gen</button>
                </form>
            </div>
        );
    }
}
export default MemeGenerator;
